import { useContext, useEffect, useState } from "react";
import { UserContext } from "../UserContext";

function Modal({ display }) {
  // const { userEmail, setUserEmail } = useContext(UserContext);
  const [isDisplay, setIsDisplay] = useState(display);

  // useEffect(() => {
  //   setIsDisplay(display);
  // }, [display]);

  return (
    <div className={`modal ${isDisplay ? "" : "hide-modal"}`} id="modal">
      <div className="close-modal-btn-container">
        <button
          onClick={() => setIsDisplay((prev) => !prev)}
          className="modal-close-btn"
          id="modal-close-btn"
        >
          X
        </button>
      </div>
      <div className="modal-inner" id="modal-inner">
        <img src={require("../images/Vector.png")} alt="camper" />
        <h2>Join the club</h2>
        <p className="modal-text" id="modal-text">
          Subscribe and Get an Extra 25% Off on your first purchase.
        </p>
        <form
          target="_blank"
          action="https://formsubmit.co/stanger307@live.com"
          method="POST"
          id="consent-form"
        >
          <label htmlFor="email"></label>
          <input
            id="email"
            name="email"
            type="email"
            placeholder="Email address"
            required
          />
          <button className="modal-btn" type="submit">
            Subscribe
          </button>
          <div className="signup">
            <label htmlFor="modal-checkbox">
              By signing up, you agree to Privacy Policy and Terms of Use.
            </label>
            <input
              id="modal-checkbox"
              type="checkbox"
              name="checkbox"
              // unchecked
            />
          </div>
        </form>
      </div>
    </div>
  );
}

export default Modal;
