import { useContext } from "react";
import { UserContext } from "../Context/UserContext";

function Modal() {
  const { userEmail, setUserEmail, isChecked, setIsChecked, setDisplay } =
    useContext(UserContext);

  const handleEmail = (event) => {
    setUserEmail(event.target.value);
  };

  return (
    <>
      <div className="close-modal-btn-container">
        <button
          onClick={() => setDisplay(false)}
          className="modal-close-btn"
          id="modal-close-btn"
        >
          X
        </button>
      </div>
      <div className="modal-inner" id="modal-inner">
        <img src={require("../images/Vector.png")} alt="camper" />
        <h2>Join the club</h2>
        <p className="modal-text" id="modal-text">
          Subscribe and Get an Extra 25% Off on your first purchase.
        </p>
        <form
          target="_blank"
          action="https://formsubmit.co/stanger307@live.com"
          method="POST"
          id="consent-form"
        >
          <label htmlFor="email"></label>
          <input
            onChange={handleEmail}
            id="email"
            name="email"
            type="email"
            value={userEmail}
            placeholder="Email address"
            required
          />
          <button className="modal-btn" type="submit">
            Subscribe
          </button>
          <div className="signup">
            <label htmlFor="modal-checkbox">
              By signing up, you agree to Privacy Policy and Terms of Use.
            </label>
            <input
              onClick={() => setIsChecked((prev) => !prev)}
              id="modal-checkbox"
              type="checkbox"
              name="checkbox"
              value={isChecked}
            />
          </div>
        </form>
      </div>
    </>
  );
}

export default Modal;
